import React, { Component } from "react";
import Reactotron from 'reactotron-react-js';
import Chart from "chart.js";
import TempChart from '../../components/chartList.js/tempChart.js';

class TroopDashboard extends Component {
    constructor(props) {
        super(props);
        this.state = {}
     }
    chartRef = React.createRef();

    generateChart(){
        const myChartRef = this.chartRef.current.getContext("2d");
        Reactotron.log("tempChart values", this.props.chartValues);
        new Chart(myChartRef, {
            type: "line",
            data: {           
                datasets: [
                    {label: "Weather", data:this.props.chartValues}
                ]
            }
        })
    }

    render(){
        Reactotron.log("tempChart props troops", this.props)

        return(
            <div>
                Testing Dashboard component 1
                <div className="graphContainer">
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
                </div>
            </div>
        )
    }
}

export default TroopDashboard;
