import React, { Component } from "react";
import Reactotron from 'reactotron-react-js';
import Chart from "chart.js";
import * as moment from 'moment';
import './tempChart.css';

Chart.defaults.global.defaultFontFamily = "'PT Sans', sans-serif"
Chart.defaults.global.legend.display = false;

class TroopDashboard extends Component {

    chartRef = React.createRef();

    componentDidMount() {
        this.buildChart()
    }

    componentDidUpdate() {
        this.buildChart()
    }

    buildChart = () => {
        const myChartRef = this.chartRef.current.getContext("2d");
        Reactotron.log("values", this.props.chartValues)
        // const { width: chartWidth } = myChartRef.canvas;

        // //---Gradient Styling---//
        // let gradientLine = myChartRef
        //     .createLinearGradient(0, 0, chartWidth, 0);
        // gradientLine.addColorStop(0.7, "#98b9ab");
        // gradientLine.addColorStop(1, "#a698b9");

        //---Gradient Styling---//
        const {height: graphHeight} = myChartRef.canvas;

        let gradientLine = myChartRef
            .createLinearGradient(0, 0, 0, graphHeight);
        gradientLine.addColorStop(0, "rgb(255, 0, 110, 0.2)");
        gradientLine.addColorStop(1, "#a698b9");
        gradientLine.addColorStop(1, "rgb(255, 0, 110, 0.7)");

        new Chart(myChartRef, {
            type: "line",
            data: {
                //Bring in data
                labels: ["Jan", "Feb", "March"],
                datasets: [
                    {
                        label: "Weather",
                        data: this.props.chartValues,
                        fill: true,
                        borderColor: gradientLine
                    }
                ]
            },
            options: {
                //Customize chart options
                responsive: true,
                maintainAspectRatio: false,
                layout: {
                    padding: {
                        top: 5,
                        left: 15,
                        right: 15,
                        bottom: 15
                    }
                },
                scales: {
                    xAxes: [{
                        ticks: { display: true },
                        gridLines: {
                            display: true,
                            drawBorder: true
                        }
                    }],
                    yAxes: [{
                        ticks: { display: true },
                        gridLines: {
                            display: true,
                            drawBorder: true
                        }
                    }]
                }
            }
        });
    }


    render() {

        Reactotron.log('this.props Troops.js', this.props)
        return (
            <div className="graphContainer">
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
            </div>
        )
    }
}

export default TroopDashboard;
