import React, { PureComponent } from "react";
import Reactotron from 'reactotron-react-js';
import TempChart from '../../components/chartList/tempChart.js'
import Button from '@material-ui/core/Button';
import './troopDashboard.css'

class TroopDashboard extends PureComponent {
    state = {day: true}

    //componentDidMount(this.props.)

    async makePostRequest(data) {
        Reactotron.log('cv data', data)
        for(var i = 0; i  <= data.chartValues; i++){
          Reactotron.log('cv', data.chartValues[i])
        }
        const body = {
          temperature: 88,
           speed: 0,
        };

        Reactotron.log('post fired')
        Reactotron.log('post data', data)
        let res = await axios.post('http://localhost:3001/api/weather', body);

        Reactotron.log(`Status code: `, res);
    }

    toggleButtonClick = e => {
        //const { value } = e.target;
        Reactotron.log("value", e.target.value)
        Reactotron.log("day state", this.state)
        this.setState({ day : !this.state.day})

    }

    render() {
        Reactotron.log('this.props Troops.js', this.props)
        return (
            <div className="container">
            <h1>Weather Dashboard</h1>
            <div className="buttonContainer">
                <Button
                    variant="contained" 
                    color="primary"
                    value="Day"
                    onClick={this.toggleButtonClick}
                >
                    Day
                </Button>
                <Button
                    variant="contained" 
                    color="primary"
                    value="Night"
                    onClick={this.toggleButtonClick}
                >
                    Night
                </Button>

            </div>
            <TempChart 
                chartValues={this.props.chartValues} 
                filteredValues={this.props.filteredValues} 
                labelValues={this.props.labelValues}
                toggle={this.state.day}
                />
        </div>
        )
    }
}

export default TroopDashboard;
