        chartValues, 
        labelValues,
        filteredValues, 
        cardData, 
        avgTemperature, 
        location,
        })

  }




  arrAvg = (arr) => {
    let length = arr.length
    let sum =  arr.reduce((a,b) => a + b)
    return Math.round(sum / length);
  }

  clickWeekly = (e) => {
    this.setState({ week: true, month: false})
  }

  clickMonthly = (e) => {
    this.setState({ week: false, month: true})
  }

