import React, { Component } from "react";
import Reactotron from 'reactotron-react-js';
import Chart from "chart.js";
import TempChart from '../../components/chartList.js/tempChart.js';

class TroopDashboard extends Component {
    chartRef = React.createRef();

    componentDidMount(){
        const myChartRef = this.chartRef.current.getContext("2d");
        Reactotron.log("tempChart values", this.props);
        new Chart(myChartRef, {
            type: "line",
            data: {           
                datasets: [
                    {label: "Weather", data:this.props.weatherData}
                ]
            }
        })
    }
    render(){
        Reactotron.log("tempChart props", this.props)

        return(
            <div>
                Testing Dashboard component 1
                <div className="graphContainer">
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
                </div>
            </div>
        )
    }
}

export default TroopDashboard;
