import React, { Component } from "react";
import Reactotron from 'reactotron-react-js';
import Chart from "chart.js";
import * as moment from 'moment';
import './tempChart.css';

const {width: graphWidth} = myChartRef.canvas;

let gradientLine = myChartRef
    .createLinearGradient(0, 0, graphWidth * 2, 0);
gradientLine.addColorStop(0, "#FF006E");
gradientLine.addColorStop(1, "#F46036");



class TroopDashboard extends Component {
    chartRef = React.createRef();

    componentDidMount() {
        this.buildChart()
    }

    componentDidUpdate() {
        this.buildChart()
    }

    buildChart = () => {
        const myChartRef = this.chartRef.current.getContext("2d");
        Reactotron.log("values", this.props.chartValues)
        new Chart(myChartRef, {
            type: "line",
            data: {
                //Bring in data
                labels: ["Jan", "Feb", "March"],
                datasets: [
                    {
                        label: "Weather",
                        data: this.props.chartValues,
                        fill: false
                    }
                ]
            },
            options: {
                //Customize chart options
                responsive: true,
                maintainAspectRatio: false,
                layout: {
                    padding: {
                        top: 5,
                        left: 15,
                        right: 15,
                        bottom: 15
                    }
                },
                scales: {
                    xAxes: [{
                        ticks: { display: true },
                        gridLines: {
                            display: true,
                            drawBorder: true
                        }
                    }],
                    yAxes: [{
                        ticks: { display: true },
                        gridLines: {
                            display: true,
                            drawBorder: true
                        }
                    }]
                }
            }
        });
    }


    render() {
        Reactotron.log('this.props Troops.js', this.props)
        return (
            <div className="graphContainer">
                <canvas
                    id="myChart"
                    ref={this.chartRef}
                />
            </div>
        )
    }
}

export default TroopDashboard;
