import React, { Component } from 'react';
import Reactotron from 'reactotron-react-js';
import {
  BrowserRouter,
  Switch,
  Route,
  Link
} from "react-router-dom";
import CardRow from './Pages/CardRow/CardRow.js';
import ShiftDetails from './Pages/Shift_Details/ShiftDetails.js';
import TroopDashboard from './Pages/Dashboard/TroopDashboard.js';
import * as moment from 'moment'
import axios from 'axios';
import logo from './logo.svg';
import './App.css';

class App extends Component{
  state = {
    forecastPeriods: [], 
    chartValues : [],
    labelValues: [],
    cardData: []
  };

  componentDidMount(){
    console.log("component mounted");
    this.fetchTableData();
  }

  async fetchTableData() {
    const response = await axios.get('https://api.weather.gov/points/30.3322,-81.6557', {
      headers: {}
    })
    Reactotron.log('response', response)
    // const cardData = await axios.get(response.data.properties)
    const forecast = await axios.get(response.data.properties)
    let labelValues = forecast.data.properties.periods.map((date) => date.isDaytime ? moment(date.startTime).format('DD') : null)
    let filteredValues = labelValues.filter((item) => item !== null);
    let chartValues = forecast.data.properties.periods.map((cv) => cv.temperature)
    this.setState({ forecastPeriods : forecast.data.properties.periods, chartValues, filteredValues, cardData })
  }



  render(){
    Reactotron.log('this.state App.js', this.state)
    return (
      <div className="App">
        <header className="App-header">
            {/* <div>
              <ul>
                <li>
                  <Link to="ShiftDetails">Shift Details</Link>
                </li>
                <li>
                  <Link to="Dashboard">Dashboard</Link>
                </li>
              </ul>
            </div> */}
            <hr />
                <CardRow forecastPeriods={this.state.forecastPeriods} />
                {/* <TroopDashboard chartValues={this.state.chartValues} filteredValues={this.state.filteredValues}/>
                <ShiftDetails weatherData={this.state.forecastPeriods} /> */}
        </header>
      </div>
    );
  }

}

export default App;
